<script>
function confirmDelete(itemName) {
    return confirm('Are you sure you want to delete \"' + itemName + '\"? This action cannot be undone.');
}

function toggleSection(sectionId) {
    var section = document.getElementById(sectionId);
    if (section.style.display === 'none') {
        section.style.display = 'block';
    } else {
        section.style.display = 'none';
    }
    return false;
}

function confirmLogout() {
    return confirm('Are you sure you want to logout?');
}

function showAbout() {
    document.getElementById('aboutModal').style.display = 'block';
    return false;
}

function closeAbout() {
    document.getElementById('aboutModal').style.display = 'none';
}

function closeEditModal() {
    document.getElementById('editModal').style.display = 'none';
    if (window.history.replaceState) {
        var url = new URL(window.location);
        url.searchParams.delete('edit');
        window.history.replaceState({}, '', url);
    }
}

function showArchiveManager() {
    document.getElementById('archiveModal').style.display = 'block';
}

function showExtractManager() {
    document.getElementById('archiveModal').style.display = 'block';
}

function closeArchiveModal() {
    document.getElementById('archiveModal').style.display = 'none';
}

function toggleSelectAll() {
    var selectAll = document.getElementById('selectAll');
    var checkboxes = document.querySelectorAll('input.bulk-checkbox');
    checkboxes.forEach(function(checkbox) {
        checkbox.checked = selectAll.checked;
    });
}

function selectAllFiles() {
    var checkboxes = document.querySelectorAll('input.bulk-checkbox');
    checkboxes.forEach(function(checkbox) {
        checkbox.checked = true;
    });
    document.getElementById('selectAll').checked = true;
}

function deselectAllFiles() {
    var checkboxes = document.querySelectorAll('input.bulk-checkbox');
    checkboxes.forEach(function(checkbox) {
        checkbox.checked = false;
    });
    document.getElementById('selectAll').checked = false;
}

function selectAllArchiveFiles() {
    var checkboxes = document.querySelectorAll('#archiveForm input.bulk-checkbox');
    checkboxes.forEach(function(checkbox) {
        checkbox.checked = true;
    });
}

document.addEventListener('click', function(event) {
    var aboutModal = document.getElementById('aboutModal');
    var editModal = document.getElementById('editModal');
    var archiveModal = document.getElementById('archiveModal');
    
    if (event.target === aboutModal) {
        closeAbout();
    }
    if (event.target === editModal) {
        closeEditModal();
    }
    if (event.target === archiveModal) {
        closeArchiveModal();
    }
});

document.addEventListener('keydown', function(event) {
    if (event.key === 'Escape') {
        var editModal = document.getElementById('editModal');
        if (editModal && editModal.style.display === 'block') {
            closeEditModal();
        }
        var aboutModal = document.getElementById('aboutModal');
        if (aboutModal && aboutModal.style.display === 'block') {
            closeAbout();
        }
        var archiveModal = document.getElementById('archiveModal');
        if (archiveModal && archiveModal.style.display === 'block') {
            closeArchiveModal();
        }
    }
    
    if ((event.ctrlKey || event.metaKey) && event.key === 's') {
        var editModal = document.getElementById('editModal');
        if (editModal && editModal.style.display === 'block') {
            event.preventDefault();
            document.getElementById('editForm').submit();
        }
    }
});

