        .about-text { line-height: 1.6; }
        .about-text h3 { color: #5af; margin-bottom: 10px; }
        .about-text p { margin-bottom: 10px; }
        .developer-info { background: #1a1a1a; padding: 15px; border-radius: 5px; margin: 10px 0; border-left: 3px solid #5af; }
        .upload-summary { margin: 10px 0; }
