        .file-info-item { display: flex; justify-content: space-between; margin-bottom: 5px; }
        .file-info-label { color: #999; }
        
        .bulk-checkbox { margin-right: 8px; }
        .feature-section { background: #1a1a1a; padding: 15px; margin: 15px 0; border-radius: 8px; border: 1px solid #444; }
        .feature-section h3 { color: #5af; margin-bottom: 15px; border-bottom: 1px solid #333; padding-bottom: 8px; }
        .feature-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 15px; margin-top: 15px; }
        .feature-item { background: #222; padding: 15px; border-radius: 5px; }
        .feature-item h4 { color: #9b59b6; margin-bottom: 10px; }
        .bulk-actions { background: #1a1a1a; padding: 15px; margin: 15px 0; border-radius: 8px; border: 1px solid #444; }
        .bulk-controls { display: flex; gap: 10px; margin-bottom: 15px; flex-wrap: wrap; align-items: center; }
        .bulk-controls select, .bulk-controls input { margin: 0; }
        .checkbox-item { display: flex; align-items: center; margin-bottom: 8px; padding: 5px; }
        .checkbox-item:hover { background: #2a2a2a; }
        
        @media (max-width: 768px) {
            .server-info { grid-template-columns: 1fr; }
            .file-table { display: block; overflow-x: auto; }
            .file-actions { flex-wrap: wrap; }
            .name-input, .permission-input, .time-input { width: 100%; }
            .logout-btn, .about-btn { position: relative; top: 0; right: 0; margin-bottom: 15px; margin-right: 10px; }
            .quick-actions { grid-template-columns: 1fr; }
            .modal-content { width: 95%; margin: 1% auto; }
            .edit-textarea { height: 60vh; }
            .feature-grid { grid-template-columns: 1fr; }
            .bulk-controls { flex-direction: column; align-items: stretch; }
        }
